import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { Button, Image, Space, Table } from 'antd';
import { DeleteOutlined, EditOutlined, GlobalOutlined } from '@ant-design/icons';
import dayjs from 'dayjs';

import { FlogData } from 'types/flog';
import { DEFAULT_POSITION } from 'constants/application';
import { Map } from 'components/Map';
import { useDispatch } from 'hooks/useDispatch';
import { useSelector } from 'hooks/useSelector';
import { useQuery } from 'hooks/useQuery';
import user from 'store/user';
import flog, { getFlogItemsRequest } from 'store/flog';
import './index.css';

const columns = [
  {
    title: 'Место',
    dataIndex: 'position',
    key: 'position',
    // render: (position: Coordinates) => (<Image src={getMapPreviewUrl(position)} />),
    render: () => (<Image
      width={100}
      height={100}
      src="error"
      fallback="data:image/png;base64,"
    />),
  }, {
    title: 'Тип',
    dataIndex: 'fishing_type',
    key: 'fishing_type',
  }, {
    title: 'Общий вес улова',
    dataIndex: 'total_weight',
    key: 'total_weight',
  }, {
    title: 'Дата',
    dataIndex: 'date',
    key: 'date',
    render: (date: string) => dayjs(date).format('D MMM YYYY(dddd)'),
  }, {
    title: 'Время',
    dataIndex: 'time',
    key: 'time',
    render: (time: string[]) => `${dayjs(time[0]).format('HH:mm')} - ${dayjs(time[1]).format('HH:mm')}`,
  }, {
    title: 'Действия',
    key: 'actions',
    render: (data: null, record: FlogData) => (
      <Space>
        <Link to={`/explore?selected=${record.id}`}><Button icon={<GlobalOutlined />} size="small" /></Link>
        <Link to={`/explore/${record.id}`}><Button icon={<EditOutlined />} size="small" /></Link>
        <Button icon={<DeleteOutlined />} size="small" onClick={() => JSON.stringify(record.id)} />
      </Space>
    ),
  },
];

const ExploreFlog = () => {
  const dispatch = useDispatch();
  const selected = useQuery('selected');
  const [mapCenter, setMapCenter] = useState(DEFAULT_POSITION);
  const isAuthorized = useSelector(user.isAuthorized);
  const flogItems = useSelector(flog.getItems);
  const getItem = useSelector(flog.getItem);

  useEffect(() => {
    if (isAuthorized) {
      dispatch(getFlogItemsRequest());
    }
  }, [dispatch, isAuthorized]);

  useEffect(() => {
    if (!selected) {
      return;
    }

    const flogItem = getItem(selected);

    if (flogItem?.position) {
      setMapCenter(flogItem?.position);
      window.scrollTo({
        top: 0,
        behavior: "smooth"
      });
    }
  }, [getItem, selected])

  return (
    <>
      <Map
        mapCenter={mapCenter}
        userPosition={selected ? mapCenter : undefined}
      />
      <br />
      <Table
        rowKey="id"
        columns={columns}
        dataSource={flogItems}
        rowClassName={(record) => record.id === selected ? 'selected' : ''}
      />
    </>
  );
};

export default ExploreFlog;
